import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { map } from 'rxjs/operators';
import { Post } from './posts/post.model';
import { User } from './auth/user.model';
import { environment } from '../environments/environment';

const BASE_URL = environment.production ? environment.api_base_url : '';

@Injectable({ providedIn: 'root' })
export class ApiProviderService {
  constructor(private httpClient: HttpClient){}

  addUser(user: User) {
    return this.httpClient.post(
      'https://labos5-a8d2f-default-rtdb.europe-west1.firebasedatabase.app/users.json',
      user
    );
  }

  getPosts() {
    return this.httpClient
      .get(
        BASE_URL + '/posts'
      )
      .pipe(
        map((res: any) => {
          const posts = [];
          for (let key in res) {
            posts.push({ ...res[key], id: key });
          }
          return posts;
        })
      );
  }

  addPost(post: Post) {
    return this.httpClient.post(
      'https://labos5-a8d2f-default-rtdb.europe-west1.firebasedatabase.app/posts.json',
      post
    );
  }

  editPost(post: Post) {
    return this.httpClient.patch(
      `https://labos5-a8d2f-default-rtdb.europe-west1.firebasedatabase.app/posts/${post.id}/.json`, post
    );
  }

  deletePost(id: String) {
    return this.httpClient.delete(
      `https://labos5-a8d2f-default-rtdb.europe-west1.firebasedatabase.app/posts/${id}.json`
    );
  }

  getUsers() {
    return this.httpClient
      .get(
        'https://labos5-a8d2f-default-rtdb.europe-west1.firebasedatabase.app/users.json'
      )
      .pipe(
        map((res: any) => {
          const users = [];
          for (let key in res) {
            users.push({ ...res[key], id: key });
          }
          return users;
        })
      );
  }
}
