import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';
import { Subject, Subscription } from 'rxjs';
import { AuthService } from '../auth/auth.service';
import { User } from '../auth/user.model';
import { Post } from './post.model';
import { PostsService } from './posts.service';

@Component({
  selector: 'app-posts',
  templateUrl: './posts.component.html',
  styleUrls: ['./posts.component.css'],
})
export class PostsComponent {
  title = 'novi-projekt';
  editNumber = -1;
  textAreaText = '';
  addPost = false;

  authenticated = false;
  authChangeSubscription: Subscription | null = null;

  newPost: Post = new Post();

  posts: Post[] = [];
  postsSubject: Subject<Post[]> = new Subject<Post[]>();

  loggedInUser: User = new User();

  constructor(
    private postsService: PostsService,
    private authService: AuthService,
    private httpClient: HttpClient
  ) {}

  ngOnInit(): void {
    this.postsSubject = this.postsService.getPosts();
    this.postsSubject.subscribe((res) => {
      this.posts = res;
    });

    this.authenticated = this.authService.isAuthenticated();
    this.loggedInUser = this.authService.getUser();

    this.authChangeSubscription = this.authService.authChange.subscribe(
      (res) => {
        this.authenticated = this.authService.isAuthenticated();
        this.loggedInUser = this.authService.getUser();
      }
    );
  }

  add() {
    if (this.loggedInUser._id.length > 0) {
      this.newPost.userId = this.loggedInUser._id;
    }
    console.log(this.loggedInUser._id);

    if (this.newPost.userId.length > 0) {
      this.postsService.addPost(this.newPost);
      this.addPost = false;
    }
  }

  edit(i: number) {
    this.textAreaText = this.posts[i].comment;
    this.editNumber = i;
  }

  saveEdit(i: number) {
    let tmpPost: Post = { ...this.posts[i] };
    tmpPost.comment = this.textAreaText;
    this.postsService.editPost(tmpPost);
    this.editNumber = -1;
  }

  delete(i: number) {
    this.postsService.deletePost(this.posts[i]._id);
    this.posts.splice(i, 1);
    this.editNumber = -1;
  }

  toggleNewPost() {
    this.addPost = !this.addPost;
  }

  getUser(userId: string) {
    return this.authService.getUserNameFromId(userId);
  }

  logout() {
    this.authService.logout();
  }
}
<div>
  <a *ngIf="authenticated"
    >Welcome
    <a [routerLink]="['profile']"
      >{{ loggedInUser.username }} ({{ loggedInUser.email }})</a
    ></a
  >
  <button *ngIf="authenticated" (click)="logout()">Logout</button>
  <p>Posts ({{ posts.length }})</p>
  <div *ngFor="let e of posts; let i = index">
    <div style="border: 1px solid black; padding: 10px">
      By {{ getUser(e.userId) }} at {{ e.timestamp }}
      <div *ngIf="editNumber != i; else ta">
        {{ e.comment }}
        <button *ngIf="e.userId == loggedInUser._id" (click)="delete(i)">
          Delete
        </button>
        <button *ngIf="e.userId == loggedInUser._id" (click)="edit(i)">
          Edit
        </button>
      </div>
      <ng-template #ta>
        <br />
        <textarea [(ngModel)]="textAreaText"></textarea>
        <br />
        <button (click)="saveEdit(i)">Save</button>
      </ng-template>
    </div>
  </div>
</div>
<button (click)="toggleNewPost()">New</button>
<div *ngIf="addPost == true">
  User<br />
  <input type="text" value="{{ loggedInUser.username }}" disabled /><br />
  Comment<br />
  <textarea [(ngModel)]="newPost.comment"></textarea><br />
  <button
    (click)="add()"
    [disabled]="newPost.comment.length < 1"
    [ngStyle]="{
      'background-color': newPost.comment.length > 0 ? '#73c7af' : 'red'
    }"
  >
    Send
  </button>
</div>
